    </tr>
  </ng-template>
  <ng-template pTemplate="body" let-user>
    <tr>
      <td>{{ user.name }}</td>
      <td>{{ user.phone }}</td>
      <td>{{ user.gender }}</td>
      <td>{{ user.address }}</td>
    </tr>
  </ng-template>
</p-table>
