import { Action } from "@ngrx/store";

const initialeState={
  users:[
    {
      name:"Fatima zahra Boujrar ",
      phone:"00212684279502",
      gender:"Female",
      address:"Marrakech 40000"
    }
  ],
  loading:false,
  loaded:true
}

export function userReducer(state=initialeState,action:Action){
  switch(action.type){
    case "LOAD_USERS":{
      return{
        ...state,
        loading:true,
        loaded:false
      }
    }
    default:{
      return state
    }
  }
}
